<template>
	<div class="pos-mask fixed" :class="{ 'ev-none': !inLoad }">
		<div class="pos-center load-con">
			<div class="weui-loading" v-if="inLoad"></div>
            <p class="load-txt">{{inLoad ? loadTxt : timeoutTxt}}</p>
		</div>
	</div>
</template>

<script>
import onCloseMix from '../mixins/onclose'

export default {
    mixins: [ onCloseMix ],
    data() {
        return {
            inLoad: true,
            loadTxt: '加载中',
            timeoutSec: 12,
            timeoutTxt: '加载超时',
            timeoutShowSec: 1.2,
        }
    },
    mounted() {
        if(this.inLoad) {
            setTimeout(() => {
                this.inLoad = false;
                this.onLoadEnd();
            }, this.timeoutSec * 1e3)
        } else {
            this.onLoadEnd();
        }
    },
    methods: {
        onLoadEnd() {
            setTimeout(() => {
                this.close()
            }, this.timeoutShowSec * 1e3);
        },
        close() {
            this.removeDom();
        },
    },
}
</script>


<style lang="less">
.ev-none {
    pointer-events: none;
}
.load-con {
    min-width: 110px;
    max-width: 200px;
    padding: 20px 10px;
    text-align: center;
    border-radius: 12px;
    background: rgba(0, 0, 0, 0.7);
    color: #f5f5f5;
}
.weui-loading {
    font-size: 35px;
    width: 1em;
    height: 1em;
    margin-bottom: 5px;
    display: inline-block;
    vertical-align: middle;
    animation: weuiLoading 1s steps(12, end) infinite;
    background: transparent url(data:image/svg+xml;base64,) no-repeat;
    background-size: 100%;
}

@keyframes weuiLoading {
    0% {transform: rotate3d(0, 0, 1, 0deg);}
    100% {transform: rotate3d(0, 0, 1, 360deg);}
}
</style>

